import React, { useState, useEffect } from "react";
import { Chart } from "react-google-charts";
import { useNavigate } from "react-router-dom";
import { db } from "./firebase";
import { collection, getDocs, addDoc } from "firebase/firestore";
import styles from './Style/AddPage.module.css'; // Import the CSS Module

const months = [
  "January", "February", "March", "April", "May", "June", 
  "July", "August", "September", "October", "November", "December"
];

const Home = () => {
  const [selectedMonth, setSelectedMonth] = useState(months[new Date().getMonth()]);
  const [expenses, setExpenses] = useState([]);
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [isAdding, setIsAdding] = useState(false);
  const [amount, setAmount] = useState('');
  const [category, setCategory] = useState('');
  const [description, setDescription] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      const querySnapshot = await getDocs(collection(db, "expenses"));
      const data = querySnapshot.docs.map(doc => doc.data());
      setExpenses(data);
    };
    fetchData();
  }, []);

  useEffect(() => {
    const total = expenses.reduce((acc, curr) => acc + curr.amount, 0);
    setTotalExpenses(total);
  }, [expenses]);

  const handleMonthChange = (e) => {
    setSelectedMonth(e.target.value);
  };

  const handleAddExpense = async () => {
    await addDoc(collection(db, "expenses"), {
      amount: Number(amount),
      category,
      description,
    });
    setAmount('');
    setCategory('');
    setDescription('');
    setIsAdding(false);
    navigate("/");
  };

  const currentDate = new Date().toLocaleDateString('en-US', {
    month: 'long',
    day: 'numeric',
    year: 'numeric',
  });

  const data = [
    ["Expense", "Amount"],
    ...expenses.map((expense, index) => [`Expense ${index + 1}`, expense.amount]),
  ];

  const options = {
    title: selectedMonth,
    pieHole: 0.4,
    pieSliceText: "label",
    slices: {
      1: { offset: 0.1 },
    },
    legend: "none",
    pieSliceTextStyle: {
      color: "black",
    },
    pieStartAngle: 100,
    tooltip: { trigger: "none" },
    chartArea: {
      left: "5%",
      top: "5%",
      width: "90%",
      height: "90%",
    },
  };

  const monthOptions = months.map((month) => (
    <option key={month} value={month}>{month}</option>
  ));

  return (
    <div>
      {!isAdding ? (
        <>
          <select value={selectedMonth} onChange={handleMonthChange}>
            {monthOptions}
          </select>
          <button onClick={() => setIsAdding(true)}>Add Expense</button>
          <Chart
            chartType="PieChart"
            data={data}
            options={options}
            width={"100%"}
            height={"400px"}
          />
          <div
            style={{
              position: "absolute",
              top: "50%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              textAlign: "center",
            }}
          >
            Total Expenses: {totalExpenses}
          </div>
        </>
      ) : (
        <div>
          <h2>{currentDate}</h2>
          <form className={styles.form} onSubmit={handleAddExpense}>
            <label className={styles.label}>
              {/* Amount: */}
              <input
                className={styles.input}
                type="number"
                placeholder="Enter amount ($CAD)"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                required
              />
            </label>
            <label className={styles.label}>
              {/* Category: */}
              <select
                className={styles.select}
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                required
              >
                <option value="" disabled hidden>Select Category</option>
                <option value="Housing">Housing</option>
                <option value="Utilities">Utilities</option>
                <option value="Transportation">Transportation</option>
                <option value="Food">Food</option>
                <option value="Healthcare">Healthcare</option>
                <option value="Clothes">Clothes</option>
                <option value="Personal Care">Personal Care</option>
                <option value="Entertainment">Entertainment</option>
                <option value="Others">Others</option>
              </select>
            </label>
            <label className={styles.label}>
              {/* Description: */}
              <input
                className={styles.input}
                type="text"
                placeholder="Enter description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                required
              />
            </label>
            <button className={styles.button} type="submit">Add Expense</button>
            <button onClick={() => setIsAdding(false)}>Cancel</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Home;
