import React, { useState } from 'react';
import styles from './Style/AddPage.module.css'; // Import the CSS Module
import { getFirestore, collection, addDoc } from "firebase/firestore";
import firebase from "./firebase";
import { useNavigate } from 'react-router-dom';

const AddPage = () => {
  const [amount, setAmount] = useState('');
  const [category, setCategory] = useState('');
  const [description, setDescription] = useState('');
  const navigate = useNavigate(); // Initialize useNavigate hook

  // Category colors
  const colors = {
    Housing: "#FFB3BA",
    Utilities: "#FFD3BA",
    Transportation: "#FFFFBA",
    Food: "#BAFFC9",
    Healthcare: "#BAE1FF",
    Clothes: "#BAC2FF",
    "Personal Care": "#D9BAFF",
    Entertainment: "#FFBACD",
    Others: "#689534",
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Form submitted:', { amount, category, description });

    // Add the expense to Firestore
    const db = getFirestore();
    await addDoc(collection(db, "expenses"), {
      amount: parseFloat(amount),
      category,
      description,
    });

    // Reset form fields
    setAmount('');
    setCategory('');
    setDescription('');

    // Show alert
    alert('Expense Added');

    // Redirect to HomePage
    navigate('/');
  };


  const currentDate = new Date().toLocaleDateString('en-US', {
    month: 'long',
    day: 'numeric',
    year: 'numeric',
  });

  return (
    <div>
      <h2>{currentDate}</h2>
      <form className={styles.form} onSubmit={handleSubmit}>
        <label className={styles.label}>
          {/* Amount: */}
          <input
            className={styles.input}
            type="number"
            placeholder="Enter amount ($CAD)"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </label>
        <label className={styles.label}>
          {/* Category: */}
          <select
            className={styles.select}
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            required
          >
            <option value="" disabled hidden>Select Category</option>
            <option value="Housing">Housing</option>
            <option value="Utilities">Utilities</option>
            <option value="Transportation">Transportation</option>
            <option value="Food">Food</option>
            <option value="Healthcare">Healthcare</option>
            <option value="Clothes">Clothes</option>
            <option value="Personal Care">Personal Care</option>
            <option value="Entertainment">Entertainment</option>
            <option value="Others">Others</option>
          </select>
        </label>
        <label className={styles.label}>
          {/* Description: */}
          <input
            className={styles.input}
            type="text"
            placeholder="Enter description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </label>
        <button
          className={styles.button}
          type="submit"
          style={{ backgroundColor: colors[category] || "gray" }}
        >
          Add Expense
        </button>
      </form>
    </div>
  );
};

export default AddPage;
