import React, { useState, useEffect } from "react";
import { Chart } from "react-google-charts";
import { useNavigate } from "react-router-dom";
import { collection, getDocs } from "firebase/firestore";
import db from './firebase'; // Import db from firebase.js
import './Style/HomePage.module.css'; // Import CSS file

const months = [
  "January", "February", "March", "April", "May", "June", 
  "July", "August", "September", "October", "November", "December"
];

const colors = {
  Housing: "#FFB3BA",
  Utilities: "#FFD3BA",
  Transportation: "#FFFFBA",
  Food: "#BAFFC9",
  Healthcare: "#BAE1FF",
  Clothes: "#BAC2FF",
  "Personal Care": "#D9BAFF",
  Entertainment: "#FFBACD",
  Others: "#689534",
};

const HomePage = () => {
    const [selectedMonth, setSelectedMonth] = useState(months[new Date().getMonth()]);
    const [expenses, setExpenses] = useState([]);
    const [totalExpenses, setTotalExpenses] = useState(0);
    const navigate = useNavigate();

    useEffect(() => {
      const fetchData = async () => {
        const querySnapshot = await getDocs(collection(db, "expenses"));
        const data = querySnapshot.docs.map(doc => doc.data());
        setExpenses(data);
      };
      fetchData();
    }, []);

    useEffect(() => {
      // Calculate total expenses whenever expenses change
      const total = expenses.reduce((acc, expense) => acc + expense.amount, 0);
      setTotalExpenses(total);
    }, [expenses]);

    const handleMonthChange = (e) => {
      setSelectedMonth(e.target.value);
    };

    const handleAddExpense = () => {
      navigate("/add");
    };

    const options = {
        title: selectedMonth,
        pieHole: 0.4,
        pieSliceText: "label",
        legend: "none",
        pieSliceTextStyle: {
          color: "black",
        },
        pieStartAngle: 100,
        tooltip: { isHtml: true },
        chartArea: {
          left: "5%",
          top: "5%",
          width: "45%", // Adjusted to make space for legend
          height: "90%",
        },
        slices: expenses.map((_, index) => ({ color: colors[expenses[index].category] || "gray" })),
      };

      const data = [
        ["Expense", "Amount", { role: "style" }, { role: "tooltip", type: "string", p: { html: true } }],
        ...expenses.map((expense, index) => {
          const color = colors[expense.category] || "gray"; // Default color if category not found
          return [expense.description, expense.amount, color, `<b>$${expense.amount.toFixed(2)}</b>`];
        }),
      ];


    const categoryList = Object.keys(colors);

    const categoryTable = (
      <table className="category-table">
        <tbody>
          {categoryList.map((category) => (
            <tr key={category}>
              <td className="category-cell" style={{ backgroundColor: colors[category] }}>{category}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );

    const monthOptions = months.map((month) => (
      <option key={month} value={month}>{month}</option>
    ));

    return (
      <div className="homepage-container">
        <div className="left-column">
          <select value={selectedMonth} onChange={handleMonthChange}>
            {monthOptions}
          </select>
          <button onClick={handleAddExpense}>Add Expense</button>
          <Chart
            className="chart"
            chartType="PieChart"
            data={data}
            options={options}
            width={"100%"}
            height={"400px"}
          />
          <div className="total-expenses">
            Total Expenses: {totalExpenses}
          </div>
          {categoryTable} {/* Include the table here */}
        </div>
      </div>
    );
  };

  export default HomePage;
