import React, { useState, useEffect } from 'react';
import { Chart } from 'react-google-charts';
import firebase from './firebase';

const months = [
  'January', 'February', 'March', 'April', 'May', 'June', 
  'July', 'August', 'September', 'October', 'November', 'December'
];

const HomePage = () => {
  const [selectedMonth, setSelectedMonth] = useState(months[0]);
  const [expenses, setExpenses] = useState([]);
  const [totalExpenses, setTotalExpenses] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      const snapshot = await firebase.database().ref('expenses').once('value');
      const data = snapshot.val() || [];
      setExpenses(data);
      const total = data.reduce((acc, curr) => acc + curr.amount, 0);
      setTotalExpenses(total);
    };

    fetchData();
  }, []);

  const handleMonthChange = (e) => {
    setSelectedMonth(e.target.value);
  };

  const handleAddExpense = async () => {
    const amount = parseFloat(prompt('Enter amount:') || 0);
    const category = prompt('Enter category:') || '';
    const description = prompt('Enter description:') || '';
    const newExpense = { amount, category, description };
    const newExpenses = [...expenses, newExpense];
    await firebase.database().ref('expenses').set(newExpenses);
    setExpenses(newExpenses);
    const total = newExpenses.reduce((acc, curr) => acc + curr.amount, 0);
    setTotalExpenses(total);
  };

  const data = [
    ['Expense', 'Amount'],
    ...expenses.map((expense, index) => [`Expense ${index + 1}`, expense.amount]),
  ];

  const options = {
    title: selectedMonth,
    pieHole: 0.4,
    pieSliceText: 'label',
    slices: {
      1: { offset: 0.1 },
    },
    legend: 'none',
    pieSliceTextStyle: {
      color: 'black',
    },
    pieStartAngle: 100,
    tooltip: { trigger: 'none' },
    chartArea: {
      left: '5%',
      top: '5%',
      width: '90%',
      height: '90%',
    },
  };

  const monthOptions = months.map((month) => (
    <option key={month} value={month}>{month}</option>
  ));

  return (
    <div>
      <select value={selectedMonth} onChange={handleMonthChange}>
        {monthOptions}
      </select>
      <button onClick={handleAddExpense}>Add Expense</button>
      <Chart
        chartType="PieChart"
        data={data}
        options={options}
        width={'100%'}
        height={'400px'}
      />
      <div
        style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          textAlign: 'center',
        }}
      >
        Total Expenses: {totalExpenses}
      </div>
    </div>
  );
};

export default HomePage;
